<!DOCTYPE HTML>  <html> 	<header>		<title>Registration</title>		<meta charset="utf-8"/>		<meta name="viewport" content="width=device-width, initial-scale=1.0"/>		<link rel = "stylesheet" href = "css/bootstrap.min.css"/>		<link rel = "stylesheet" href = "css/styles.css"/>		<script src="js/bootstrap.min.js"></script>		<script src="js/jquery.min.js"></script>	</header><body><!-- ******************start wrapper class********************* -->	<div class="wrapper"><!-- ******************start container********************* -->	<div class="container">		<div class="login_main col-md-offset-2 col-md-8">			<div class="col-md-12 col-sm-12  reg_header text-center">				<h2>Registration Form</h2><!-- ******************start menu of index page ********************* -->									<div class="menu col-md-offset-7 col-md-4 text-right">					<ul>						<li><a href="signin.php"> Log In</a></li>					</ul>				</div>			</div><!-- ******************registration form for newly comming user********************* -->		<form class = "form-horizontal" method="post" action="index.php" enctype="multipart/form-data"> <!-- ******************start backend php code for registration********************* -->			<?php 				include 'config.php'; 				$success_rmessage=$error_message="";				$fastname = $lastname= $email=$session=$roll=$subj = $pass="" ;				$fastnameErr = $lastnameErr= $emailErr=$sessionErr=$rollErr=$subjErr = $passErr=$imgerror="" ;				$err1= $err2= $err3=$err4=$err5=$err6 = $err7="" ;				$errors = array();/* ******************input field validation********************* */				if (isset($_POST['send'])) 				{							if (!preg_match("/^[a-zA-Z ]*$/", $_POST['fastname']))					{						$errors['fastnameErr']="Please enter your valid first name";						$err1 =$errors['fastnameErr'];					}					if (!preg_match("/^[a-zA-Z ]*$/", $_POST['lastname']))					{						$errors['lastnameErr']="Please enter your valid lastt name";						$err2 =$errors['lastnameErr'];					}					 if (!filter_var($_POST['email'],FILTER_VALIDATE_EMAIL))					{							$errors['emailErr']="invalid email";						$err3 =$errors['emailErr'];					}					if (!preg_match("/^[0-9-]*$/", $_POST['session']))					{						$errors['sessionErr']="invalid session";						$err4 =$errors['sessionErr'];					}					if ( !preg_match("/^[0-9]*$/", $_POST['roll']) )					{						$errors['rollErr']="roll number must be integer";						$err5 =$errors['rollErr'];					}					if (!preg_match("/^[a-zA-Z]*$/", $_POST['subj']))					{						$errors['subjErr']="invalid subject name";						$err6 =$errors['subjErr'];					}															$fastname = $_POST['fastname'];						$lastname = $_POST['lastname'];						$email = $_POST['email'];						$session = $_POST['session'];						$roll = $_POST['roll'];						$subj = $_POST['subj'];						$pass = MD5($_POST['pass']);						$email_exists = mysql_query("SELECT email FROM registraion WHERE email = '$email' ");/* ******************check, if any error occurs ********************* */					if(empty($errors))					{/* ******************check, if any email that is newly creates, are exist or not in database ********************* */						if( mysql_num_rows($email_exists)>0)						{	 session_start();							echo "email already exist";						}/* ******************check, if any email are not exist do continue********************* */						else 						{	/* ******************uploading image********************************************** */							$file_name=$_FILES["image_file"]["name"];							$file_ext = substr( $file_name, strripos($file_name, '.') );/* ******************check the extention of uploaded file(png/jpg)format*********************** */							if( ($file_ext  == ".png") || ($file_ext == ".jpg"))							{/* ******************if check the extention of uploaded file(png/jpg)format ok do contt... *********************** */								$target_path1= 'upload/'.$file_name;								$target_path=move_uploaded_file($_FILES["image_file"]["tmp_name"],'upload/'.$file_name);								$Query = "INSERT INTO registraion ( fastname, lastname, email, session, roll, subj, pass,image_name) 										VALUES ( '$fastname', '$lastname', '$email', '$session', '$roll', '$subj','$pass','".$target_path1."' )";								mysql_query($Query);								$success_rmessage ="sucessfully inserted";															}							else							{																$imgerror= "your uploaded file must be in png or jpg format and size must be smaller than 10KB";							}											}					}				}								?><!-- **************************start code in html for newer registration*************************-->			<div class="form-group  ">					<label class=" col-md-2 col-md-offset-2 text-right" for="usrFN" > First Name: </label>					<div class="col-md-5  ">						<input type = "text" class = "form-control " id="usrFN" placeholder="enter first name" name="fastname" value='<?php echo $fastname;?>' required>						<p class="error"><?php echo $err1;?></p>					</div>				</div>								<div class="form-group">					<label for="usrLN" class="col-md-2 col-md-offset-2 text-right" >Last Name: </label>					<div class="col-md-5 ">						<input type = "text" class = "form-control " id="usrLN" placeholder="enter last name" name="lastname" value= '<?php echo $lastname;?>' required>						<p class="error"><?php echo $err2;?></p>					</div>									</div>									<div class="form-group">					<label for="Email" class="col-md-2 col-md-offset-2 text-right" >Email: </label>					<div class="col-md-5 ">						<input type = "text" class = "form-control col-md-6" id="Email" placeholder="enter email" name="email" value='<?php echo $email;?>' required>						<p class="error"><?php echo $err3;?></p>					</div>				</div>								<div class="form-group">					<label for="SESSION" class="col-md-2 col-md-offset-2 text-right">Session: </label>					<div class="col-md-5 ">						<input type = "text" class = "form-control col-md-6 datepicker" id="SESSION" placeholder="enter session" name="session" value='<?php echo $session;?>' required>						<p class="error"><?php echo $err4;?></p>					</div>				</div>								<div class="form-group">					<label for="ROLL" class="col-md-2 col-md-offset-2 text-right">Roll: </label>					<div class="col-md-5 ">						<input type = "text" class = "form-control col-md-6" id="ROLL" placeholder="enter roll" name="roll" value='<?php echo $roll;?>' required>						<p class="error"><?php echo $err5;?></p>					</div>									</div>								<div class="form-group">					<label for="sjt" class="col-md-2 col-md-offset-2 text-right">Subject: </label>					<div class="col-md-5 ">						<input type = "text" class = "form-control col-md-6" id="sjt" placeholder="enter subject" name="subj" value='<?php echo $subj;?>' required>						<p class="error"><?php echo $err6;?></p>					</div>									</div>									<div class="form-group">					<label for="pwd" class="col-md-2 col-md-offset-2 text-right col-md-6">Password: </label>					<div class="col-md-5 ">						<input type = "password" class = "form-control" id="pwd" placeholder="enter password" name="pass" value='<?php echo $pass;?>' required>											</div>				</div>				<div class="form-group">					<label class="col-md-2 col-md-offset-2 text-right col-md-6">Image: </label>					<input name="image_file" type="file" required>					<p class="error col-md-offset-1 "><?php echo $imgerror;?></p>				</div>								<div class="form-group" > 					<div class="col-md-offset-1 text-center">						<input class="btn btn-primary" type="submit" value="Create" name="send">										</div>				</div>				<?php 					if(isset($success_rmessage))					{						echo $success_rmessage;					}											echo $error_message;									?>			</form>		</div>			</div>	</div>	</body></html>