<?php 					$fastname = $lastname= $email=$session=$roll=$subj = $pass="" ;		$fastnameErr = $lastnameErr= $emailErr=$sessionErr=$rollErr=$subjErr = $passErr="" ;/* form validation done here------********************************--------------------------------------------- */		if( $_SERVER["REQUEST_METHOD"] == "POST")  // here post is in uppercase.		{			$naming_pattern = "/^[a-zA-Z ]*$/ ";			$roll_num_pattern = "/^[0-9]*$/ ";			$session_pattern ="/^[0-9-]*$/";			$subject_pattern ="/^[a-zA-Z -]*$/";			$password_pattern = '/(?=.*\d)(?=.*[a-z])(?=.*[A-Z]).{8,}/';			if(empty($_POST["fastname"]))			{				$fastnameErr = "First Name Is Required";							}			else			{				$fastname = test_input($_POST["fastname"]);			}			/*validate firstname----------------------------------------*/			if( !preg_match($naming_pattern, $fastname) )			{				$fastnameErr = "only letter and white space are allowed.";			}/*  fastname section end */			/*.......................................----------------------------------------------------------------------------..............................*/						if(empty($_POST["lastname"]))			{				$lastnameErr = "Last Name Is Required";			}			else			{				$lastname = test_input($_POST["lastname"]);			}			/*validate lastname-----------------------------------------*/			if( !preg_match($naming_pattern,$lastname) )			{				$lastnameErr = "only letter and white space are allowed.";			}/*  lastname section end */			/*.......................................----------------------------------------------------------------------------..............................*/						if(empty($_POST["email"]))			{				$emailErr = "Email Is Required";			}			else			{				$email = test_input($_POST["email"]);			}			/*validate email-------------------------------------------------*/			if( !filter_var($email,FILTER_VALIDATE_EMAIL) )			{				$emailErr = "Invalid email format.";			}/*  email section end */			/*.......................................----------------------------------------------------------------------------..............................*/						if(empty($_POST["session"]))			{				$sessionErr = "Session Is Required";			}			else			{				$session = test_input($_POST["session"]);			}			/*validate session---------------------------------*/			if( !preg_match($session_pattern,$session) )			{				$sessionErr = "Invalid session";			}/*  session section end */			/*.......................................----------------------------------------------------------------------------..............................*/						if(empty($_POST["roll"]))			{				$rollErr = "Roll Number Is Required";			}			else			{				$roll = test_input($_POST["roll"]);			}			/*roll number validate-----------------------------*/			if (!preg_match($roll_num_pattern,$roll)) 			{				$rollErr = "Roll Number should be positive";			}/*  roll section end */			/*.......................................----------------------------------------------------------------------------..............................*/						if(empty($_POST["subj"]))			{				$subjErr = "Subject Field Is Required";			}			else			{				$subj = test_input($_POST["subj"]);			}			/*subject validate----------------------------------*/			if( !preg_match($subject_pattern,$subj))			{				$subjErr = "Invalid subject name.";			}/*  subject section end */			/*.......................................----------------------------------------------------------------------------..............................*/									if(empty($_POST["pass"]))			{				$passErr ="Password Is Required";			}			else			{				$pass = test_input($_POST["pass"]);			}			/*password validate----------------------------------*/			if( !preg_match($password_pattern,$pass) )			{				$passErr = "password is at least 8 char long, contains at least one of each of lowercase, uppercase and a number";			}/*  password section end */			/*.......................................----------------------------------------------------------------------------..............................*/									}				function test_input($data)		{			$data = trim($data);			$data = stripslashes($data);			$data = htmlspecialchars($data);			return $data;		}				/* ****************************************//*******************************************************/	?>