 <?php /* *****************start login*************** */	include 'config.php';				$messErr=$passwordErr=$emailErr=$Password=$Email="";	if( isset($_POST['submit']) )  // here post is in uppercase.	{						if(empty($_POST['email']))			{				$emailErr.= "Email field cannot Be empty";			}			if(empty($_POST['pass']))			{				$passwordErr.= "password field cannot Be empty";			}			$Email = $_POST['email'];			$Password= $_POST['pass'];			$Pwd =MD5($Password);						$statement = $db->prepare("select * from registraion where email=? and pass=? ");			$statement->execute(array($Email,$Pwd));			$num = $statement->rowCount();			//var_dump($num); exit;			if($num>0)			{				session_start();				$_SESSION['email'] =$Email;				$_SESSION['pass'] =$Pwd;								$results=$statement->fetchAll();				foreach($results as $row)					{						if($row['type']==2)					{												$_SESSION['id']=$row['st_id'];												header('location:adminprofile.php');											}					else					{	//?id='.$row['st_id']						$_SESSION['id']=$row['st_id'];												header('location:userprofile.php');											}				}			}			else			{				$messErr ="Invalid Username or Password";			}	}	?><!DOCTYPE HTML> <html> 	<header>		<title>Log In Form</title>		<meta charset="utf-8"/>		<meta name="viewport" content="width=device-width, initial-scale=1.0"/>		<link rel = "stylesheet" href = "css/bootstrap.min.css"/>		<link rel = "stylesheet" href = "css/styles.css"/>		<script src="js/bootstrap.min.js"></script>		<script src="js/jquery.min.js"></script>	</header>		<body>	<div class="wrapper">		<div class="container">			<div class="sign_main col-md-offset-3 col-md-6">				<div class="col-md-12 col-sm-12  reg_header text-center">					<h2>User Log In</h2>				</div><!-- ******************html form for log in**************-->				<form class = "form-horizontal" name="loginform" method="post" action="signin.php" >  					<div class="form-group text-center">						<div class="col-md-6 col-md-offset-3">							<span class="error"><?php echo  $emailErr;?></span>							<input type = "text" class = "form-control col-md-6"  placeholder="enter your email id" name="email">						</div>					</div>										<div class="form-group text-center" id="pass">						<div class="col-md-6 col-md-offset-3">						 <span class="error"><?php echo  $passwordErr;?></span>							<input type = "password" class = "form-control"  placeholder="enter your account password" name="pass">													</div>					</div>										<div class="form-group" > 						<div class="lgbtn  text-center ">						     <span class="error"><?php echo  $messErr;?></span>							<input class="btn btn-primary BUTTON col-md-3  col-md-offset-5" type="submit" name="submit" value="Log In">							<a href="index.php">Create New Account</a><br>							<input type="hidden" name="id" value="<?php echo $id;?>">						</div>											</div>									</form>			</div>		</div>	</div></body></html>