<?php 	function random_token() 	{		$salt = 'abcdefghijklABCDEFGHIJKmnopqr4567stuvwxyzLMNOPQRSTUVWXYZ123890';		$pass = array(); //remember to declare $pass as an array		$alphaLength = strlen($salt) - 1; //put the length -1 in cache		for ($i = 0; $i<32; $i++)		{			$rnd= rand(0, $alphaLength);			$pass[] = $salt[$rnd];		}		return implode($pass); //turn the array into a string			}		$key="bangladeshncrypt";	$string="mcrypt_101encrypt";		/***********	##	encrypt function	************/	function encrypt($string,$key)	{			$string= rtrim(base64_encode(mcrypt_encrypt(MCRYPT_RIJNDAEL_256,$key,$string,MCRYPT_MODE_ECB)));		return $string;	} 	/***********	##	decrypt function	************/	function decrypt($string,$key)	{				$string= rtrim(mcrypt_decrypt(MCRYPT_RIJNDAEL_256,$key,base64_decode($string),MCRYPT_MODE_ECB));		return $string;	}?>